<script>
	export let size = 512;

	let colors = [
		'#30DFE0',
		'#4D49FB',
		'#67FEB0',
		'#721C1B',
		'#877A7A',
		'#C5BAFF',
		'#E5FF97',
		'#FE7236',
		'#FF00E7',
		'#FF3737',
		'#FFCAC0'
	];

	function shuffleArray(array) {
		for (let i = array.length - 1; i > 0; i--) {
			const j = Math.floor(Math.random() * (i + 1));
			[array[i], array[j]] = [array[j], array[i]];
		}
		return array;
	}

	export function shuffleColors() {
		colors = shuffleArray([...colors]); // Create a new array to trigger reactivity
	}

	let lastMouseX = null;
	let lastMouseY = null;

	function handleMouseMove(event) {
		const x = event.clientX;
		const y = event.clientY;

		if (lastMouseX === null || lastMouseY === null) {
			// First mouse movement over the SVG
			lastMouseX = x;
			lastMouseY = y;
			shuffleColors();
		} else {
			const dx = x - lastMouseX;
			const dy = y - lastMouseY;
			const distanceSquared = dx * dx + dy * dy;
			const thresholdSquared = 8 * 8; // 4 pixels threshold squared

			if (distanceSquared >= thresholdSquared) {
				// Update the last known position
				lastMouseX = x;
				lastMouseY = y;
				shuffleColors();
			}
		}
	}

	function handleMouseLeave() {
		// Reset the last known mouse position when the cursor leaves the SVG
		lastMouseX = null;
		lastMouseY = null;
	}
</script>

<svg
	height={size}
	viewBox="0 0 593 220"
	fill="none"
	xmlns="http://www.w3.org/2000/svg"
	on:mousemove={handleMouseMove}
	on:mouseleave={handleMouseLeave}
	role="complementary"
